package MUL;

import java.io.*;
import java.math.BigInteger;


public class Main {

    public static final int REGULAR_DIVIDE_TRESHOLD = 5000;

    public static BigInteger karatsuba(BigInteger x, BigInteger y) {

        // cutoff to brute force
        int N = Math.max(x.bitLength(), y.bitLength());

        if (N <= REGULAR_DIVIDE_TRESHOLD) {
            return x.multiply(y);                // optimize this parameter
        }
        // number of bits divided by 2, rounded up
        N = (N / 2) + (N % 2);

        // x = a + 2^N b,   y = c + 2^N d
        BigInteger b = x.shiftRight(N);
        BigInteger a = x.subtract(b.shiftLeft(N));
        BigInteger d = y.shiftRight(N);
        BigInteger c = y.subtract(d.shiftLeft(N));

        // compute sub-expressions
        BigInteger ac = karatsuba(a, c);
        BigInteger bd = karatsuba(b, d);
        BigInteger abcd = karatsuba(a.add(b), c.add(d));

        return ac.add(abcd.subtract(ac).subtract(bd).shiftLeft(N)).add(bd.shiftLeft(2 * N));
    }

    public static void main(String[] args) throws IOException {

        BufferedReader r = new BufferedReader(new InputStreamReader(System.in));

        int n = Integer.parseInt(r.readLine());

        BufferedWriter log = new BufferedWriter(new OutputStreamWriter(System.out));


        for (int i = 0; i < n; i++) {
            String s = r.readLine();
            int spaceIndex = s.indexOf(' ');
            BigInteger x = new BigInteger(s.substring(0, spaceIndex), 10);
            BigInteger y = new BigInteger(s.substring(spaceIndex + 1), 10);
            BigInteger res = karatsuba(x, y);
            log.write(res.toString());
            log.newLine();
        }
        log.flush();
        r.close();
    }
}

